import GetAllCategories from "../productComponent/GetAllCategories";
import CategoryNavigator from "../productComponent/CategoryNavigator";
import { useParams } from "react-router-dom";
import axios from "axios";
import { useEffect, useState } from "react";
import ProductCard from "../productComponent/ProductCard";
import { ToastContainer, toast } from "react-toastify";

const Product = () => {
  const { productId, categoryId } = useParams();

  let user = JSON.parse(sessionStorage.getItem("active-user"));

  const [quantity, setQuantity] = useState("");
  const [products, setProducts] = useState([]);
  const [product, setProduct] = useState({
    id: "",
    title: "",
    description: "",
    quantity: "",
    price: "",
    imageName: "",
    category: { id: "", title: "" },
  });

  const retrieveProduct = async () => {
    const response = await axios.get(
      "http://localhost:8080/api/product/id?productId=" + productId
    );
    return response.data;
  };

  useEffect(() => {
    const getProduct = async () => {
      const retrievedProduct = await retrieveProduct();
      setProduct(retrievedProduct);
    };

    const getProductsByCategory = async () => {
      const allProducts = await retrieveProductsByCategory();
      if (allProducts) {
        setProducts(allProducts);
      }
    };

    getProduct();
    getProductsByCategory();
  }, [productId]);

  const retrieveProductsByCategory = async () => {
    const response = await axios.get(
      "http://localhost:8080/api/product/category?categoryId=" + categoryId
    );
    return response.data;
  };

  const saveProductToCart = (userId) => {
    fetch("http://localhost:8080/api/user/cart/add", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        quantity: quantity,
        userId: userId,
        productId: productId,
      }),
    }).then((result) => {
      toast.success("Products added to Cart Successfully!!!", {
        position: "top-center",
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
      });

      result.json().then((res) => {
        console.log("response", res);
      });
    });
  };

  const addToCart = (e) => {
    if (user == null) {
      alert("Please login to buy the products!!!");
      e.preventDefault();
    } else {
      saveProductToCart(user.id);
      setQuantity("");
      e.preventDefault();
    }
  };

  return (
    <div className="container-fluid">
      <style>
        {`
          .custom-bg {
            background-color: #f9f9f9;
            border-radius: 8px;
            padding: 20px;
            box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
          }
          .custom-bg-text {
            color: #007bff;
          }
          .border-color {
            border: 1px solid #007bff;
          }
          .bg-color {
            background-color: #e9ecef;
          }
          .text-color {
            color: #333;
          }
          .admin {
            box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
          }
          h1, h2, h3, h4 {
            font-family: 'Arial', sans-serif;
            margin: 0;
          }
          .btn {
            background-color: #007bff;
            color: white;
            border: none;
            border-radius: 4px;
            padding: 10px 20px;
            transition: background-color 0.3s, transform 0.2s;
          }
          .btn:hover {
            background-color: #0056b3;
            transform: translateY(-2px);
          }
          .card-img-top {
            transition: transform 0.2s;
            border-radius: 8px;
          }
          .card-img-top:hover {
            transform: scale(1.05);
          }
          .product-title {
            font-weight: bold;
            font-size: 1.5rem;
            margin-bottom: 0.5rem;
          }
          .product-description {
            font-size: 1rem;
            margin: 10px 0;
          }
          .product-price {
            font-size: 1.5rem;
            font-weight: bold;
            color: #007bff;
          }
          .product-stock {
            font-size: 1rem;
            color: #666;
          }
          .related-products-title {
            margin-top: 20px;
            font-size: 1.5rem;
          }
        `}
      </style>

      <div className="row">
        <div className="col-sm-2 mt-2">
          <GetAllCategories />
        </div>
        <div className="col-sm-3 mt-2 admin">
          <div className="card form-card border-color custom-bg">
            <img
              src={"http://localhost:8080/api/product/" + product.imageName}
              style={{
                maxHeight: "400px",
                maxWidth: "100%",
                width: "auto",
              }}
              className="card-img-top mx-auto d-block m-2"
              alt="Product"
            />
          </div>
        </div>
        <div className="col-sm-7 mt-2">
          <div className="card form-card border-color custom-bg">
            <div className="card-header bg-color">
              <h1 className="custom-bg-text product-title">{product.title}</h1>
            </div>

            <div className="card-body text-left text-color">
              <div className="text-left mt-3">
                <h3>Description :</h3>
              </div>
              <p className="product-description">{product.description}</p>
            </div>

            <div className="card-footer custom-bg">
              <div className="text-center text-color">
                <p className="product-price">Price : &#8377;{product.price}</p>
                <p className="product-stock">Stock : {product.quantity}</p>
              </div>
              <div className="d-flex justify-content-between">
                <form className="row g-3" onSubmit={addToCart}>
                  <div className="col-auto">
                    <input
                      type="number"
                      className="form-control"
                      id="addToCart"
                      placeholder="Enter Quantity..."
                      min={1}
                      max={product.quantity}
                      onChange={(e) => setQuantity(e.target.value)}
                      value={quantity}
                      required
                    />
                  </div>
                  <div className="col-auto">
                    <input
                      type="submit"
                      className="btn mb-3"
                      value="Add to Cart"
                    />
                    <ToastContainer />
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="row mt-2">
        <div className="col-sm-2"></div>
        <div className="col-sm-10">
          <h2 className="related-products-title">Related Products:</h2>
          <div className="row row-cols-1 row-cols-md-4 g-4">
            {products.map((product) => {
              return <ProductCard item={product} key={product.id} />;
            })}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Product;
